import React, {Component} from 'react';


class ProjectCase extends Component{
    constructor(props){
        super(props)
        this.state={
            openModal:false
        }
        this.slideIndex = 1
    }

    showSlides=(slideIndex)=>{

    }

    currentSlide=(n)=>{
        showSlides(this.slideIndex = n);
    }

    showSlides=(n)=> {
        var i;
        var slides = document.getElementsByClassName("mySlides");


        for (i = 0; i < slides.length; i++) {
            slides[i].style.display = "none";
        }

        slides[slideIndex-1].style.display = "block";

      }


    openModal = () =>{
        this.setState({openModal:!this.state.openModal})
        console.log()
        if(this.state.openModal){
            document.getElementById('myModal').style.display = "block"
        }else{
            document.getElementById('myModal').style.display = "none"
        }
    }


    closeModal=()=>{
        this.setState({openModal:false})
        if(!this.state.openModal){
            document.getElementById('myModal').style.display = "none"
        }
    }

    linkto=(e)=> {
        console.log(e)
        if(e){
             window.location.href = e;
        }
}

    componentDidMount(){
        this.openModal()
    }
    render(){

        const style={
            display:'visible'
        }


        return(
            <React.Fragment>
            {
                this.props.projectCase.map((pc) =>
                (
                <div>       
                {/* image used to open light box */}
                    <div className="project_lightBox">
                        <div className="column">
                            <img src={`image/${pc.img}`} onClick={(event)=>{this.openModal();this.currentSlide(`${pc.sid}`)}} alt="picture" style={style}/>
                        </div>
                        <div className="project_content">
                            <h4>{pc.name}</h4>
                            <p>{pc.introduction}</p>
                            <p onClick={this.linkto.bind(this, `${pc.github}`)}>{pc.github}</p>
                        </div>
                    </div>

                {/* this is light box part */}
                <div id={`myModal`} className="modal mySlide">
                    <div className="modal-content">
                    <span className="close cursor" onClick={this.closeModal}>&times;</span>

                        <div>
                            <img src={`image/${pc.img}`} alt="picture"/>
                        </div>
                        <div className="caption-container">
                            <h5>{pc.name}</h5>
                            <p><span>Purpose:</span>{pc.introduction}</p>
                            <p><span>Github:</span>{pc.github}</p>
                        </div>
                    </div>
                </div>
                        </div>
                            )
                        )
                    }




            </React.Fragment>
        )
    }
}



export default ProjectCase
