import React, {Component} from 'react';
import {Link} from 'react-router-dom'

class ProjectCase extends Component{
    constructor(props){
        super(props)
        this.state={
            openModal:false
        }
        console.log(this.state.openModal)
    }

    openModal = () =>{
        this.setState({openModal:!this.state.openModal})
        console.log()
        if(this.state.openModal){
            document.getElementById('myModal').style.display = "block"
        }else{
            document.getElementById('myModal').style.display = "none"
        }
    }

    closeModal=()=>{
        this.setState({openModal:false})
        console.log(this.state.openModal)
        if(!this.state.openModal){
            document.getElementById('myModal').style.display = "none"
        }
    }

    componentDidMount(){
        this.openModal()
    }
    render(){
        const style={
            display:'visible'
        }

        return(
            <React.Fragment>

                <div className="harryPotter">
                    <h4>HarryPotter</h4>

                    {/* image used to open light box */}
                    <div className="project_lightBox">
                        <div className="column">
                            <img src="/image/hr.png" onClick={this.openModal} className="hover-shadow" style={style}/>
                        </div>
                    </div>

                </div>
                <div id="myModal" className="modal">
                    <div className="modal-content">
                    <span className="close cursor" onClick={this.closeModal}>&times;</span>

                        <div className="mySlides">
                            <img src="/image/hr.png"/>
                        </div>
                        <div className="caption-container">
                            <h5>Harrt Potter</h5>
                            <p><span>Purpose:</span>Effect practice</p>
                            <p><span>Github:</span></p>

                        </div>
                    </div>
                </div>
                }
            </React.Fragment>
        )
    }
}



export default ProjectCase
