import React, {Component} from 'react';
import {Link} from 'react-router-dom'

class ProjectCase extends Component{
    constructor(props){
        super(props)
        this.state={
            openModal:false
        }
    }

    openModal = () =>{
        this.setState({openModal:!this.state.openModal})
        console.log()
        if(this.state.openModal){
            document.getElementById('myModal').style.display = "block"
        }else{
            document.getElementById('myModal').style.display = "none"
        }
    }

    closeModal=()=>{
        // var slideIndex = 1,
        // showSlides =slideIndex;

        // showSlides(slideIndex = n);
        this.setState({openModal:false})
        console.log(this.state.openModal)
        if(!this.state.openModal){
            document.getElementById('myModal').style.display = "none"
        }
    }

    componentDidMount(){
        this.openModal()
    }
    render(){

        const style={
            display:'visible'
        }
        console.log(this.props)

        return(
            <React.Fragment>
            {
                this.props.projectCase.map((pc) =>
                (
                    <div>
                    <div className="project">
                    <h4>{pc.name}</h4>

                {/* image used to open light box */}
                    <div className="project_lightBox">
                        <div className="column">
                            <img src={`image/${pc.img}`} onClick={(event)=>{this.openModal();}} alt="picture" style={style}/>
                        </div>
                    </div>
                </div>
                <div id={`myModal`} className="modal">
                    <div className="modal-content">
                    <span className="close cursor" onClick={this.closeModal}>&times;</span>

                        <div>
                            <img src={`image/${pc.img}`} alt="picture"/>
                        </div>
                        <div className="caption-container">
                            <h5>{pc.name}</h5>
                            <p><span>Purpose:</span>Effect practice</p>
                            <p><span>Github:</span></p>
                        </div>
                    </div>
                </div>
                        </div>
                            )
                        )
                    }




            </React.Fragment>
        )
    }
}



export default ProjectCase
